(function () {
  const res = rq.response.json();

  rq.test("Login successful", () => {
    rq.expect(res.accessToken).to.exist;
    rq.expect(res.refreshToken).to.exist;
  });

  // Store tokens for later requests
  rq.environment.set("ACCESS_TOKEN", res.accessToken);
  rq.environment.set("REFRESH_TOKEN", res.refreshToken);

  rq.environment.set("AUTH_USER", res.username);

  console.log("Access token received:", res.accessToken);
})();
